components/beautify/navigation-list:
{{#if children}}
<a class="navPages-action-toggle" href="#"
    data-nav-tab="navPages-{{#if page_type '==' 'page'}}page-{{getVar 'navWebPageCount'}}{{/if}}{{id}}{{idSuffix}}">
    <span class="_more is-srOnly"><span class="is-srOnly">{{lang 'common.nav_more'}}</span><svg class="icon"><use xlink:href="#icon-add"></use></svg></span>
    <span class="_less is-srOnly"><span class="is-srOnly">{{lang 'common.nav_back'}}</span><svg class="icon"><use xlink:href="#icon-minus"></use></svg></span>
    {{name}}
</a>
<a class="navPages-action has-subMenu u-hideTablet u-hideMobile {{#if menu_type '==' 'vertical'}}navPages-action--vertical{{/if}}" href="{{url}}"
    href="{{url}}"
    {{#if menu_type '!=' 'vertical'}}data-instantload{{/if}}
    {{#if id '==' category.id}}data-current-category{{/if}}
>{{name}}</a>
<a class="navPages-action-toggle-desktop" href="#" data-collapsible="navPages-{{#if page_type '==' 'page'}}page-{{getVar 'navWebPageCount'}}{{/if}}{{id}}{{idSuffix}}" title="{{lang 'common.nav_more'}}"><svg class="icon"><use xlink:href="#icon-caret-down"></use></svg></a>
<div class="navPage-subMenu
        {{#if menu_type '==' 'vertical'}}
            navPage-subMenu--vertical
        {{else if menu_type '==' 'standard'}}
            navPage-subMenu--standard
        {{else if menu_type '==' 'column'}}
            navPage-subMenu--column
        {{/if}}
    " id="navPages-{{#if page_type '==' 'page'}}page-{{getVar 'navWebPageCount'}}{{/if}}{{id}}{{idSuffix}}">
    {{#if page_type '!==' 'page'}}
        <div class="navPage-subMenu-top">
            {{{region name=(concat (concat "navPages_" id) "_top--global")}}}
        </div>
    {{/if}}
    <div class="navPages-all-mobile">
        <a href="{{url}}" class="u-hideDesktop">{{lang 'common.view_all'}} <svg class="icon"><use xlink:href="#icon-arrow-view-all"></use></svg></a>
    </div>
    <div class="navPage-subMenu-middle">
        {{#if page_type '!==' 'page'}}
            {{{region name=(concat (concat "navPages_" id) "_left--global")}}}
        {{/if}}
        <ul class="navPage-subMenu-list">
            {{#each children}}
                <!-- {{incrementVar 'navWebPageCount'}} -->
                <li class="navPage-subMenu-item">
                    {{{region name=(concat (concat "navPages_" id) "_top--global")}}}
                    {{#if children}}
                        {{#if ../../theme_settings.navPages_catImg}}
                            {{#if image}}
                                <a href="{{url}}" class="u-hideMobile u-hideTablet navPage-linkImg">
                                    {{> components/kitchenary/navigation-list-img
                                        image=image
                                        lazyload=../../../../theme_settings.lazyload_mode
                                        default_alt=name
                                    }}
                                </a>
                            {{/if}}
                        {{/if}}
                        <a class="navPages-action-toggle" href="#"
                            data-collapsible="navPages-{{#if ../../page_type '==' 'page'}}page-{{getVar 'navWebPageCount'}}{{/if}}{{id}}{{../../idSuffix}}">
                            <span class="_more is-srOnly"><span class="is-srOnly">{{lang 'common.nav_more'}}</span><svg class="icon"><use xlink:href="#icon-add"></use></svg></span>
                            <span class="_less is-srOnly"><span class="is-srOnly">{{lang 'common.nav_less'}}</span><svg class="icon"><use xlink:href="#icon-minus"></use></svg></span>
                            <svg class="icon _icon-viewAll is-srOnly u-hideDesktop"><use xlink:href="#icon-arrow-view-all"></use></svg>
                            {{#if ../../theme_settings.navPages_catImg}}
                                {{#if image}}
                                    <div class="_imgTitle">
                                        {{> components/kitchenary/navigation-list-img
                                            image=image
                                            lazyload=../../../../theme_settings.lazyload_mode
                                            default_alt=name
                                        }}
                                    </div>
                                {{/if}}
                            {{/if}}
                            <span>{{name}}</span>
                        </a>
                        <a
                            class="navPage-subMenu-action has-subMenu u-hideMobile u-hideTablet"
                            href="{{url}}"
                            {{#if ../../menu_type '!=' 'vertical'}}data-instantload{{/if}}
                            {{#if id '==' ../../category.id}}data-current-category{{/if}}
                            >
                                <span>{{name}}</span>
                        </a>
                        <a class="navPages-action-toggle-desktop" tabindex="0" href="#" data-collapsible="navPages-{{#if ../../page_type '==' 'page'}}page-{{getVar 'navWebPageCount'}}{{/if}}{{id}}{{../../idSuffix}}" title="{{lang 'common.nav_more'}}">
                            {{#if theme_settings.navPages_type '==' 'megamenu'}}
                                <span>{{name}}</span>
                            {{else}}
                                <svg class="icon"><use xlink:href="#icon-caret-down"></use></svg>
                            {{/if}}
                        </a>
                        <ul class="navPage-childList" id="navPages-{{#if ../../page_type '==' 'page'}}page-{{getVar 'navWebPageCount'}}{{/if}}{{id}}{{../../idSuffix}}">
                            <div class="_titleSubmenu {{#unless theme_settings.navPages_type '==' 'megamenu'}}u-hideDesktop{{/unless}}">
                                <a class="navPages-action-toggle" href="#"
                                    data-collapsible-close="navPages-{{#if ../../page_type '==' 'page'}}page-{{getVar 'navWebPageCount'}}{{/if}}{{id}}{{../../idSuffix}}">
                                    <span class="_more is-srOnly"><span class="is-srOnly">{{lang 'common.nav_more'}}</span><svg class="icon"><use xlink:href="#icon-add"></use></svg></span>
                                    <span class="_less is-srOnly"><span class="is-srOnly">{{lang 'common.nav_less'}}</span><svg class="icon"><use xlink:href="#icon-minus"></use></svg></span>
                                    <svg class="icon _icon-viewAll u-hideDesktop"><use xlink:href="#icon-arrow-view-all"></use></svg>
                                    <span>{{name}}</span>
                                </a>
                                <a
                                class="navPage-subMenu-action has-subMenu"
                                href="{{url}}"
                                {{#if ../../menu_type '!=' 'vertical'}}data-instantload{{/if}}
                                {{#if id '==' ../../category.id}}data-current-category{{/if}}
                                >
                                    <span class="u-hideMobile u-hideTablet">{{name}}</span>
                                    <span class="u-hideDesktop">{{lang 'common.view_all'}}</span>
                                    <svg class="icon u-hideDesktop"><use xlink:href="#icon-arrow-view-all"></use></svg>
                                </a>
                            </div>
                            {{#each children}}
                                <!-- {{incrementVar 'navWebPageCount'}} -->
                                <li class="navPage-childList-item">
                                    {{> components/beautify/navigation-list-child
                                            category_id=../../../category.id
                                            idSuffix=../../../idSuffix
                                            theme_settings=../../../theme_settings
                                            menu_type=../../../menu_type
                                            page_type=../../../page_type}}
                                </li>
                            {{/each}}
                            {{!--
                            <!--
                            {{#if ../../menu_type '==' 'mega'}}
                                <li class="navPage-childList-item">
                                    <a class="navPage-childList-action" href="{{url}}" data-instantload>{{lang 'common.view_all'}}</a>
                                </li>
                            {{/if}}
                            -->
                            --}}
                            <a href="#" class="_hideToggle u-hideDesktop"></a>
                        </ul>
                    {{else}}
                        <a class="navPage-subMenu-action" href="{{url}}" data-instantload {{#if id '==' ../../category.id}}data-current-category{{/if}}>
                            {{#if ../../theme_settings.navPages_catImg}}
                                {{#if image}}
                                    <div class="_imgTitle">
                                        {{> components/kitchenary/navigation-list-img
                                            image=image
                                            lazyload=../../../../theme_settings.lazyload_mode
                                            default_alt=name
                                        }}
                                    </div>
                                {{/if}}
                            {{/if}}
                            <span>{{name}}</span>
                        </a>
                    {{/if}}
                    {{#if ../page_type '!==' 'page'}}
                        {{{region name=(concat (concat "navPages_" id) "_bottom--global")}}}
                    {{/if}}
                </li>
            {{/each}}
        </ul>
        {{#if page_type '!==' 'page'}}
            {{{region name=(concat (concat "navPages_" id) "_right--global")}}}
        {{/if}}
    </div>
    {{#if page_type '!==' 'page'}}
        <div class="navPage-subMenu-bottom">
            {{{region name=(concat (concat "navPages_" id) "_bottom--global")}}}
        </div>
    {{/if}}
</div>
{{else}}
<a class="navPages-action" href="{{url}}" data-instantload {{#if id '==' category.id}}data-current-category{{/if}}>{{name}}</a>
{{/if}}

components/common/footer:
{{#or banners.bottom theme_settings.instantload}}
{{#if settings.data_tag_enabled}}
<div class="banners--bottom" data-banner-location="bottom">
    <div class="container">
        {{#each (limit banners.bottom_metadata 1)}}
        <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}"
            data-position="{{this.location}}" data-name="{{this.banner-name}}">
            <div data-event-type="promotion-click">
                {{{this.content}}}
            </div>
        </div>
        {{/each}}
    </div>
</div>
{{else}}
<div class="banners--bottom" data-banner-location="bottom">
    <div class="container">
        <div class="banner">
            {{{limit banners.bottom 1}}}
        </div>
    </div>
</div>
{{/if}}
{{/or}}

{{{region name="footer_before"}}}
{{{region name="footer_before--global"}}}

<footer class="footer" role="contentinfo">
    <h2 class="is-srOnly">{{lang 'footer.footer_start'}}</h2>
    {{#if theme_settings.footer_show_customer_service}}
        {{#unless page_type '===' 'default'}}
            {{> components/kitchenary/sections/customer-service}}
            {{{region name="footer_customer_service--global"}}}
        {{/unless}}
    {{/if}}
    {{#and settings.show_newsletter_box theme_settings.footer_show_newsletter}}
    {{#if theme_settings.footer-secondary-style '!=' 'footer'}}
        {{#if settings.show_newsletter_box}}
            {{> components/beautify/section-home-newsletter}}
        {{/if}}
    {{/if}}
    {{/and}}

    {{{region name="footer_top--global"}}}
    {{#if theme_settings.footer_show_contactInfo}}
    <section class="footer-map">
        <div class="container">
            <div class="footer-map--list">
                {{#if theme_settings.footer_show_gmap}}
                    {{> components/beautify/section-gmap index=index}}
                {{else}}
                    {{{region name="footer_contact_left--global"}}}
                {{/if}}
                <div class="footer-map__col footer-map__col--info">
                    <div class="footer-map__text">
                        <h3 class="footer-info-heading">
                            {{theme_settings.footer_contactInfo_heading}}
                        </h3>
                        <div class="__item">
                            <div class="__item--icon">
                                <svg class="icon">
                                    <use xlink:href="#icon-place" />
                                </svg>
                            </div>
                            <div class="__item--info">
                                <p class="text">
                                    {{settings.address}}
                                </p>
                                {{#if settings.phone_number}}
                                    <p class="text">
                                        {{lang 'footer.phone'}}: <a href="tel:{{settings.phone_number}}">{{settings.phone_number}}</a>
                                    </p>
                                {{/if}}
                                {{#if theme_settings.footer_email '!==' ''}}
                                    <p class="text">
                                        {{lang 'footer.email'}}: <a href=mailto:{{theme_settings.footer_email}}>{{theme_settings.footer_email}}</a>
                                    </p>
                                {{/if}}
                            </div>
                        </div>
                        {{{region name="footer_contact--global"}}}
                    </div>
                    {{#if theme_settings.social_icon_placement_bottom}}
                    {{> components/common/social-links}}
                    {{/if}}
                </div>
            </div>
        </div>
    </section>
    {{/if}}
    <section
        class="footer-info{{#if theme_settings.footer_show_newsletter}}{{#if theme_settings.footer-secondary-style '==' 'footer'}} _has-newsletter{{/if}}{{/if}} ">
        <div class="container ">
            <div class="footer-info--list">
                {{#if theme_settings.footer_show_pages '>' -1}}
                <article class="footer-info--navigate footer-info--col">
                    {{#if theme_settings.footer_pages_heading}}
                    <h3 class="footer-info-heading">
                        <a href="#">
                            {{theme_settings.footer_pages_heading}}
                        </a>
                    </h3>
                    {{/if}}
                    {{#if theme_settings.footer_show_pages '>' 0}}
                    <ul class="footer-info-list footer-list--navigate ">
                        {{#each (limit pages theme_settings.footer_show_pages)}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                    {{/if}}
                    {{{region name="footer_below_col2--global"}}}
                </article>
                {{/if}}

                {{#if theme_settings.footer_show_categories '>' -1}}
                <article class="footer-info--category footer-info--col ">
                    {{#if theme_settings.footer_categories_heading}}
                    <h3 class="footer-info-heading">
                        <a href="#">
                            {{theme_settings.footer_categories_heading}}
                        </a>
                    </h3>
                    {{/if}}
                    {{#if theme_settings.footer_show_categories '>' 0}}
                    <ul class="footer-info-list footer-list--category">
                        {{#each (limit categories theme_settings.footer_show_categories)}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                    </ul>
                    {{/if}}
                    {{{region name="footer_below_col3--global"}}}
                </article>
                {{/if}}

                {{#if theme_settings.footer_shop_by_brand '>' -1}}
                <article class="footer-info--brands footer-info--col">
                    {{#if theme_settings.footer_brands_heading}}
                    <h3 class="footer-info-heading">
                        <a href="#">
                            {{theme_settings.footer_brands_heading}}
                        </a>
                    </h3>
                    {{/if}}
                    {{#if theme_settings.footer_shop_by_brand '>' 0}}
                    <ul class="footer-info-list footer-list--brands">
                        {{#each (limit shop_by_brand theme_settings.footer_shop_by_brand)}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                        {{/each}}
                        <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                    </ul>
                    {{/if}}
                    {{{region name="footer_below_col4--global"}}}
                </article>
                {{/if}}
            </div>
            {{#if theme_settings.footer_show_contactInfo '!==' true}}
            {{#if theme_settings.social_icon_placement_bottom}}
            {{> components/common/social-links}}
            {{/if}}
            {{/if}} 
        </div>
    </section>
    {{{region name="footer_below_columns--global"}}}
    <div class="footer-below">
        <div class="container">
            <div class="footer-copyright">
                <div class="beautify__abovePoweredBy">
                    {{{region name="footer_above_powerby--global"}}}
                </div>
                <div class="powered-by">
                    <div class="_col">
                        {{#if theme_settings.show_copyright_footer}}
                        <p class="powered-by-text _col-copyright-footer">
                            &copy; <span id="copyright_year"></span> {{settings.store_name}}.
                            <script>
                                document.getElementById("copyright_year").innerHTML = new Date().getFullYear();
                            </script>
                        </p>
                        {{/if}}
                        {{#if theme_settings.show_powered_by}}
                        <p class="powered-by-text _col-powered-by">
                            {{lang 'footer.powered_by'}}
                            <a href="http://bigcommerce.com" rel="nofollow">BigCommerce.</a>
                            Theme designed by
                            <a href="https://papathemes.com" rel="nofollow">Papathemes.</a>
                        </p>
                        {{/if}}
                        {{{region name="footer_powered_by--global"}}}
                    </div>
                    {{{region name="footer_copyright--global"}}}
                </div>
            </div>
            <div class="footer-payments">
                {{> components/common/payment-icons}}
                {{> components/beautify/footer-icons}}
            </div>
        </div>
    </div>
</footer>
{{{region name="footer_bottom--global"}}}
{{#if settings.show_newsletter_box}}
{{#if theme_settings.nl_popup_show '===' 'home'}}
{{#if page_type '===' 'default'}}
{{> components/papathemes/newsletter/modal}}
{{/if}}
{{else if theme_settings.nl_popup_show '===' 'all'}}
{{> components/papathemes/newsletter/modal}}
{{/if}}
{{/if}}

components/common/header/logo:
{{#if template_file '===' 'pages/home'}}
<h1 class="header-logo{{#if settings.store_logo.image}} _isImg{{/if}}">
    {{> components/common/store-logo}}
</h1>
{{else}}
<div class="header-logo{{#if settings.store_logo.image}} _isImg{{/if}}">
    {{> components/common/store-logo}}
</div>
{{/if}}

components/common/header/og:
{{#contains head.meta_tags "og:image"}}
{{else}}
    {{#if page_type '==' 'category'}}
        {{#if category.image}}
            <meta property="og:image" content="{{getImage category.image 'category_size'}}" />
        {{/if}}
    {{else if page_type '==' 'brand'}}
        {{#if brand.image}}
            <meta property="og:image" content="{{getImage brand.image 'brand_main_size'}}" />
        {{/if}}
    {{/if}}
{{/contains}}

components/common/paginator:
{{#or previous links next}}
<div class="pagination">
    {{#if total}}
    {{#if start}}
    {{#if end}}
        <div class="pagination-info">
            {{#if end '>' total}}
                {{{lang 'common.paginator.items_of' start=start end=total total=total}}}
            {{else}}
                {{{lang 'common.paginator.items_of' start=start end=end total=total}}}
            {{/if}}
        </div>
    {{/if}}
     {{/if}}
     {{/if}}
    <ul class="pagination-list">
        {{#if previous}}
            <li class="pagination-item pagination-item--previous">
                <a class="pagination-link"
                    href="{{previous}}"
                    {{#if instantload}}data-instantload{{/if}}
                    {{#unless reload}}data-faceted-search-facet{{/unless}}
                    aria-label="{{lang 'common.previous'}}"
                >
                    <i class="icon" aria-hidden="true">
                        <svg>
                            <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-left"></use>
                        </svg>
                    </i>
                    <span>{{lang 'common.previous'}}</span>
                </a>
            </li>
        {{/if}}
        {{#each links}}
            <li class="pagination-item {{#if this.number '==' ../current}}pagination-item--current{{/if}}">
                <a class="pagination-link" 
                    href="{{#if this.url}}{{this.url}}{{else}}{{this.link}}{{/if}}"
                    {{#if ../instantload}}data-instantload{{/if}}
                    {{#unless ../reload}}data-faceted-search-facet{{/unless}}
                    {{#if this.number '==' ../current}}
                        aria-current="page"
                        data-pagination-current-page-link
                    {{/if}}
                    aria-label="{{lang 'common.paginator.page_of' current=this.number total=../links.length}}"
                >
                    {{this.number}}
                </a>
            </li>
        {{/each}}

        {{#if next}}
            <li class="pagination-item pagination-item--next">
                <a class="pagination-link"
                    href="{{next}}"
                    {{#if instantload}}data-instantload{{/if}}
                    {{#unless reload}}data-faceted-search-facet{{/unless}}
                    aria-label="{{lang 'common.next'}}"
                >
                    <span>{{lang 'common.next'}}</span>
                    <i class="icon" aria-hidden="true">
                        <svg>
                            <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-chevron-right"></use>
                        </svg>
                    </i>
                </a>
            </li>
        {{/if}}
    </ul>
</div>
{{/or}}

components/common/social-links:
{{#if social_media}}
    <ul class="socialLinks--alt socialLinks {{#if style}}socialLinks--{{style}}{{/if}}">
        {{#each social_media}}
            {{#if display_name '==' 'twitter'}}
                <li class="socialLinks-item">
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener" title="X">
                        <span class="aria-description--hidden">X</span>
                        <svg><use xlink:href="#icon-x" /></svg>
                    </a>
                </li>
            {{else}}
                <li class="socialLinks-item">
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener" title="{{capitalize display_name}}">
                        <span class="aria-description--hidden">{{{capitalize display_name}}}</span>
                        <svg><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                </li>
            {{/if}}
        {{/each}}
    </ul>
{{/if}}

components/kitchenary/sections/customer-:
{{#or theme_settings.footer_email theme_settings.footer_whatsapp settings.phone_number}}
<div class="beautify__section {{#if index}}beautify__section--index{{index}}{{/if}} beautify__section--customer-service">
    <div class="container">
        {{#unless hide_heading}}
        <h2 class="page-heading">{{theme_settings.homepage_service_heading}}</h2>
        {{/unless}}

        {{#if region_name}}
        {{{region name=region_name}}}
        {{/if}}

        <div class="body-service">
            {{#if theme_settings.footer_email}}
            <a class="customer-service" href="mailto:{{ theme_settings.footer_email }}" title="{{ lang 'customer_service.email_address' email=theme_settings.footer_email }}">
                {{#if theme_settings.variant '!==' 'game' }}
                    <svg class="icon">
                        <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-email"></use>
                    </svg>   
                {{else}}         
                    <img class="icon" src="{{cdn 'img/enjoy-email.png'}}" alt="{{ lang 'customer_service.email' }}" title="{{ lang 'customer_service.email' }}" />
                {{/if}}
                <span class="title-service">{{ lang 'customer_service.email' }}</span>
            </a>
            {{/if}}
            {{#if theme_settings.footer_whatsapp}}
            <a class="customer-service" href="mailto:{{ theme_settings.footer_whatsapp }}" title="{{ lang 'customer_service.whatsapp_address' whatsapp=theme_settings.footer_whatsapp }}">
                {{#if theme_settings.variant '!==' 'game' }} 
                    <svg class="icon">
                        <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-live-chat"></use>
                    </svg>
                {{else}}
                    <img class="icon" src="{{cdn 'img/enjoy-live.png'}}" alt="{{lang 'customer_service.whatsapp'}}" title="{{lang 'customer_service.whatsapp'}}" />
                 {{/if}}
                <span class="title-service">{{ lang 'customer_service.whatsapp' }}</span>
            </a>
            {{/if}}
            {{#if settings.phone_number}}
            <a class="customer-service" href="tel:{{settings.phone_number}}" title="{{ lang 'customer_service.call_us' phone_number=settings.phone_number }}">
                 {{#if theme_settings.variant '!==' 'game' }}
                    <svg class="icon">
                        <use xmlns:xlink="http://www.w3.org/1999/xlink" xlink:href="#icon-phone-service"></use>
                    </svg>
                {{else}}
                    <img class="icon icon-contact" src="{{cdn 'img/enjoy-phone.png'}}" alt="{{ settings.phone_number }}" title="{{ settings.phone_number }}" />
                {{/if}}
                <span class="title-service">{{ settings.phone_number }}</span>
            </a>
            {{/if}}
        </div>
    </div>
</div>
{{/or}}

components/mooncat/category-tree:
{{#if theme_settings.categorypage_layout '===' 'left-sidebar'}}
    {{assignVar 'hideSubcategoriesSidebar' 1}}
{{/if}}
<div class="sidebarBlock sidebarBlock--categoryTree">
    <nav class="categoryTree" id="categoryTree">
        <ul class="navPages-list">
            {{#each categories}}
                <li class="navPages-item navPages-item--id-{{id}}">
                    {{> components/mooncat/category-tree-list idSuffix='-categoryTree'}}
                </li>
            {{/each}}
        </ul>
    </nav>
</div>

components/mooncat/category-tree-list:
{{#if children}}
    <a class="navPages-action-toggle" href="#"
        data-collapsible="navPages-{{id}}{{idSuffix}}">
        <span class="_more"><span class="is-srOnly">{{lang 'common.nav_more'}}</span><svg class="icon"><use xlink:href="#icon-add"></use></svg></span>
        <span class="_less"><span class="is-srOnly">{{lang 'common.nav_back'}}</span><svg class="icon"><use xlink:href="#icon-minus"></use></svg></span>
    </a>
