                                                            aria-live="polite">
                                                        <button class="button button--icon" data-action="inc">
                                                            <span class="is-srOnly">{{lang 'products.quantity_increase'}}</span>
                                                            <i class="icon" aria-hidden="true">
                                                                <svg>
                                                                    <use xlink:href="#icon-keyboard-arrow-up"/>
                                                                </svg>
                                                            </i>
                                                        </button>
                                                    </div>
                                                </div>
