    <div class="productView-summaryWrapper" data-also-bought-parent-scope>
        <div class="container">
            <ul class="productView-summaryWrapper-list">
                {{#if (merge this __language='')}}{{/if}}
                {{assignVar 'checkPlatform' 0}}
                {{assignVar '__platform' 0}}
                {{assignVar '__activationLabel' 0}}
                {{assignVar '__activationLink' 0}}

                {{#each product.custom_fields}}
                    {{#if (lowercase name) '==' 'platform'}}{{assignVar '__platform' value}}{{assignVar 'checkPlatform' 1}}{{/if}}
                    {{#if (lowercase name) '==' 'activation label'}}{{assignVar '__activationLabel' value}}{{/if}}
                    {{#if (lowercase name) '==' 'activation link'}}{{assignVar '__activationLink' value}}{{/if}}
                    {{#if (lowercase name) '==' 'language'}}{{#if (merge ../../this __language=(get 'string' (concat ../../__language value)))}}{{/if}}{{/if}}

                    {{#or
                        (if (lowercase name) '==' 'version')
                        (if (lowercase name) '==' 'developer')
                        (if (lowercase name) '==' 'release date')
                        (if (lowercase name) '==' 'publisher')
                        (if (lowercase name) '==' 'type')
                        (if (lowercase name) '==' 'delivery')}}
                        <li>
