                    <a class="productView-description-tabHeading" data-tab-mobile href="#tab-reviews-{{product.id}}">{{lang 'products.reviews.title' total=product.num_reviews}}<svg class="icon"><use xlink:href="#icon-product-tab-arrow"></svg></a>
                    <div class="productView-description-tabContent">
                        {{> components/products/reviews reviews=product.reviews product=product urls=urls}}
                    </div>
                    {{#unless in_quickview}}</div><!-- .container -->{{/unless}}
                </div>
            {{/if}}
        </article>
    </div>
</div>

components/products/ratings:
{{#for 1 5}}
