</ul>
--}}

<div class="tabs-contents">
    {{#if product.related_products}}
        <div role="tabpanel" class="tab-content has-jsContent is-active" id="tab-related">
            <h2 class="beautify__page-heading">{{theme_settings.productpage_related_products_title}}</h2>
            <div class="container">
                <div class="beautify__page-content">
                    {{#if settings.data_tag_enabled}}
                        {{#if theme_settings.productpage_related_products_type '==' 'carousel'}}
                            {{> components/products/carousel products=product.related_products list="Related Products"}}
                        {{else}}
                            <ul class="productGrid productGrid--responsiveCol" id="productGrid-tab-related">
                                {{#each products=product.related_products}}
                                    <li class="product">
                                        {{>components/products/card 
                                            show_compare=../show_compare 
                                            show_rating=../settings.show_product_rating
                                            theme_settings=../theme_settings 
                                            settings=../settings 
                                            customer=../customer}}
                                    </li>
                                {{/each}}
                            </ul>
                            <div class="__actionGroup">
                                <a href="#" class="button button--small button--outline loadMore" data-toggle="productGrid-tab-related">{{lang
                                    'common.load_more'}}</a>
                                <a href="#tab-related" class="button button--small button--outline collapse" data-toggle="productGrid-tab-related" data-toggle-anchor>{{lang
                                    'common.collapse'}}</a>
                            </div>
                        {{/if}}
                    {{else}}
                        {{#if theme_settings.productpage_related_products_type '==' 'carousel'}}
                            {{> components/products/carousel products=product.related_products}}
                        {{else}}
                            <ul class="productGrid productGrid--responsiveCol" id="productGrid-tab-related">
                                {{#each product.related_products}}
                                    <li class="product">
                                        {{>components/products/card 
                                            show_compare=../show_compare 
                                            show_rating=../settings.show_product_rating
                                            theme_settings=../theme_settings 
                                            settings=../settings 
                                            customer=../customer}}
                                    </li>
                                {{/each}}
                            </ul>
                            <div class="__actionGroup">
                                <a href="#" class="button button--small button--outline loadMore" data-toggle="productGrid-tab-related">{{lang
                                    'common.load_more'}}</a>
                                <a href="#tab-related" class="button button--small button--outline collapse" data-toggle="productGrid-tab-related" data-toggle-anchor>{{lang
                                    'common.collapse'}}</a>
                            </div>
                        {{/if}}
                    {{/if}}
                </div>
            </div>
        </div>
    {{/if}}

    {{{region name="product_below_related"}}}
    {{{region name="product_below_related--global"}}}

    {{#if product.similar_by_views}}
        <div role="tabpanel" class="tab-content has-jsContent is-active" id="tab-similar">
            <h2 class="beautify__page-heading">{{theme_settings.productpage_similar_by_views_title}}</h2>
            <div class="container">
                <div class="beautify__page-content">
                    {{#if theme_settings.productpage_similar_by_views_type '==' 'carousel'}}
                        {{> components/products/carousel products=product.similar_by_views list="Customers Also Viewed"}}
                    {{else}}
                        <ul class="productGrid productGrid--responsiveCol" id="productGrid-tab-similar">
                            {{#each product.similar_by_views}}
                                <li class="product">
                                    {{>components/products/card 
                                        show_compare=../show_compare 
                                        show_rating=../settings.show_product_rating
                                        theme_settings=../theme_settings 
                                        settings=../settings 
                                        customer=../customer}}
                                </li>
                            {{/each}}
                        </ul>
                        <div class="__actionGroup">
                            <a href="#" class="button button--small button--outline loadMore" data-toggle="productGrid-tab-similar">{{lang
                                'common.load_more'}}</a>
                            <a href="#tab-similar" class="button button--small button--outline collapse" data-toggle="productGrid-tab-similar" data-toggle-anchor>{{lang
                                'common.collapse'}}</a>
