pages/home:
---
products:
    new:
        limit: {{theme_settings.product_new_count}}
    featured:
        limit: {{theme_settings.product_featured_count}}
    top_sellers:
        limit: {{theme_settings.product_top_count}}
carousel: {{theme_settings.homepage_show_carousel}}
blog:
    recent_posts:
        limit: {{theme_settings.homepage_blog_posts_count}}
shop_by_brand:
    limit: {{theme_settings.sidebar_shop_by_brand}}
---
{{#partial "hero"}}
    {{{region name="home_below_menu"}}}

    <div class="container">
        {{#each shipping_messages}}
            {{> components/common/alert-info message}}
        {{/each}}
    </div>
    <div class="heroCarousel-container-bg">
        <div class="container container--papathemes-section1-{{theme_settings.homepage_carousel_style}}" id="papathemes-section1-container">
            <div class="papathemes-section1 papathemes-section1--{{theme_settings.homepage_carousel_style}}">
                {{> components/papathemes/sections/section section="slideshow"}}
            </div>
        </div>
    </div>
    {{{snippet 'home_content'}}}
    {{{region name="home_below_carousel"}}}
    <div class="main full">
        {{> components/papathemes/sections/section section=theme_settings.section1 index=1}}
        {{{region name="home_below_section1"}}}
        {{> components/papathemes/sections/section section=theme_settings.section2 index=2}}
        {{{region name="home_below_section2"}}}
        {{> components/papathemes/sections/section section=theme_settings.section3 index=3}}
        {{{region name="home_below_section3"}}}
        {{> components/papathemes/sections/section section=theme_settings.section4 index=4}}
        {{{region name="home_below_section4"}}}
        {{> components/papathemes/sections/section section=theme_settings.section5 index=5}}
        {{{region name="home_below_section5"}}}
        {{> components/papathemes/sections/section section=theme_settings.section6 index=6}}
        {{{region name="home_below_section6"}}}
        {{> components/papathemes/sections/section section=theme_settings.section7 index=7}}
        {{{region name="home_below_section7"}}}
        {{> components/papathemes/sections/section section=theme_settings.section8 index=8}}
        {{{region name="home_below_section8"}}}
        {{> components/papathemes/sections/section section=theme_settings.section9 index=9}}
        {{{region name="home_below_section9"}}}
        {{> components/papathemes/sections/section section=theme_settings.section10 index=10}}
        {{{region name="home_below_section10"}}}
    </div>
{{/partial}}
{{#and theme_settings.instantload is_ajax}}
    {{> layout/instantload-base}}
{{else}}
    {{> layout/base}}
{{/and}}

components/papathemes/sections/section:
{{#if section '===' '-'}}

{{else if section '===' 'slideshow'}}
    {{#if carousel}}
        {{> components/carousel}}
    {{/if}}

{{else if section '===' 'blog_recent'}}
    {{#if theme_settings.homepage_blog_posts_count '>' 0}}
    {{> components/beautify/recent-blog posts=(limit blog.recent_posts theme_settings.homepage_blog_posts_count) index=index}}
    {{/if}}

{{else if section '===' 'shopping_guide'}}
    {{#if theme_settings.homepage_blog_posts_count '>' 0}}
    {{> components/beautify/shopping-guide-blog posts=(limit blog.recent_posts theme_settings.homepage_blog_posts_count) index=index}}
    {{/if}}

{{else if section '===' 'top_categories'}}
    {{> components/beautify/top-categories index=index}}

{{else if section '===' 'new_products'}}
    {{#if products.new}}
        <div class="beautify__section kitchenary__productView {{#if index}}beautify__section--index{{index}}{{/if}} beautify__section--newProducts beautify__section--displayType-{{theme_settings.product_new_type}}">
        <div class="container">
        {{> components/products/new
            products=products.new
            show_categories=theme_settings.product_new_cats
            region_name="home_above_new_products"
            display_type=theme_settings.product_new_type}}
        </div>
        </div>
    {{/if}}

{{else if section '===' 'featured_products'}}
    {{#if products.featured}}
        <div class="beautify__section kitchenary__productView {{#if index}}beautify__section--index{{index}}{{/if}} beautify__section--featuredProducts beautify__section--displayType-{{theme_settings.product_featured_type}}">
        <div class="container">
        {{> components/products/featured
            products=products.featured
            show_categories=theme_settings.product_featured_cats
            region_name="home_above_featured_products"
            display_type=theme_settings.product_featured_type}}
        </div>
        </div>
    {{/if}}
{{else if section '===' 'customer_service'}}
    {{> components/kitchenary/sections/customer-service}}
{{else if section '===' 'popular_products'}}
    {{#if products.top_sellers}}
        <div class="beautify__section kitchenary__productView {{#if index}}beautify__section--index{{index}}{{/if}} beautify__section--topProducts beautify__section--displayType-{{theme_settings.product_top_type}}">
        <div class="container">
        {{> components/products/top
            products=products.top_sellers
            show_categories=theme_settings.product_top_cats
            region_name="home_above_top_products"
            display_type=theme_settings.product_top_type}}
        </div>
        </div>
    {{/if}}

{{else if section '===' 'recentviewed'}}
    <div class="beautify__section kitchenary__productView {{#if index}}beautify__section--index{{index}}{{/if}} beautify__section--recentlyViewedProducts beautify__section--displayType-{{theme_settings.product_recentviewed_type}}">
    <div class="container">
    {{> components/products/recentviewed
        index=index
        region_name="home_above_recentviewed_products"
        display_type=theme_settings.product_recentviewed_type}}
    </div>
    </div>

{{else if section '===' 'spt'}}
    {{#or theme_settings.spt_show_featured theme_settings.spt_show_top theme_settings.spt_show_new}}
        {{#or theme_settings.spt_init_count theme_settings.spt_lazy_count}}
            <div class="beautify__section {{#if index}}beautify__section--index{{index}}{{/if}} beautify__section--specialProductsTabs">
            <div class="container">
                {{> components/papathemes/special-products-tabs/section}}
            </div>
            </div>
        {{/or}}
    {{/or}}

{{else if section '===' 'pbcst'}}
    {{#if theme_settings.pbcst_catIDs}}
        {{inject 'haspbcst' true}}
        <div class="beautify__section {{#if index}}beautify__section--index{{index}}{{/if}} beautify__section--pbcst">
            <div class="container">
                <div class="papathemes-pbcst"
                    data-pbcst-group="{{theme_settings.pbcst_catIDs}}"
                    data-sort="{{theme_settings.pbcst_active}}"
                    data-limit="{{theme_settings.pbcst_products}}"
                    {{#if theme_settings.pbcst_lazy}}data-lazy{{/if}}>
                    {{#if theme_settings.pbcst_showBanner}}
                        {{#each (split theme_settings.pbcst_catIDs ',')}}
                            <div data-pbcst-widget-region="{{@index}}">{{{region name=(concat "pbcst_" @index)}}}</div>
                        {{/each}}
                    {{/if}}
                    {{#each (split theme_settings.pbcst_catIDs ',')}}
                        <div data-pbcst-widget-region="below_{{@index}}">{{{region name=(concat "pbcst_below_" @index)}}}</div>
                    {{/each}}
                </div>
            </div>
        </div>
    {{else if theme_settings.pbcst_count '>' 0}}
        {{inject 'haspbcst' true}}
        <div class="beautify__section {{#if index}}beautify__section--index{{index}}{{/if}} beautify__section--pbcst">
            <div class="container">
                <div class="papathemes-pbcst"
                    data-pbcst-group="{{join (pluck (limit categories theme_settings.pbcst_count) 'id') ','}}"
                    data-sort="{{theme_settings.pbcst_active}}"
                    data-limit="{{theme_settings.pbcst_products}}"
                    {{#if theme_settings.pbcst_lazy}}data-lazy{{/if}}>
                    {{#if theme_settings.pbcst_showBanner}}
                        {{#each (limit categories theme_settings.pbcst_count)}}
                            <div data-pbcst-widget-region="{{@index}}">{{{region name=(concat "pbcst_" @index)}}}</div>
                        {{/each}}
                    {{/if}}
                    {{#each (limit categories theme_settings.pbcst_count)}}
                        <div data-pbcst-widget-region="below_{{@index}}">{{{region name=(concat "pbcst_below_" @index)}}}</div>
                    {{/each}}
                </div>
            </div>
        </div>
    {{/if}}
{{else if section '===' 'featured_brands'}}
    {{#if shop_by_brand.length '>' 0}}
        {{#if theme_settings.featured_brands_show_logo}}
            <div id="kitchenary__featuredBrands" class="beautify__section kitchenary__featuredBrands {{#if index}}beautify__section--index{{index}}{{/if}} productCarousel--responsiveCol blaze-slider"
                {{#if settings.data_tag_enabled}} data-list-name="{{list}}"{{/if}}
                data-blaze-slider='{
                    "all": {
                        "enableAutoplay": true,
                        "autoplayInterval": 5000,
                        "transitionDuration": 500,
                        "slidesToShow": 6,
                        "slidesToScroll": 6,
                        "slideGap": "30px",
                        "loop": true
                    },
                    "(max-width: 1260px)": {
                        "slidesToShow": 4,
                        "slidesToScroll": 4
                    },
                    "(max-width: 800px)": {
                        "slidesToShow": 3.5,
                        "slidesToScroll": 3,
                        "slideGap": "16px"
                    },
                    "(max-width: 550px)": {
                        "slidesToShow": 2.5,
                        "slidesToScroll": 2
                    }
                }'
            >
                <div class="container">
                    {{#if theme_settings.featured_brands_heading}}
                        <h2 class="page-heading">{{theme_settings.featured_brands_heading}}</h2>
                    {{else}}
                        <h2 class="page-heading">{{lang 'brand.featured_brands'}}</h2>
                    {{/if}}
                    <div class="blaze-container">
                        <button class="blaze-button blaze-prev" aria-label="{{lang 'common.previous'}}"></button>
                        <div class="blaze-track-container">
                            <div class="blaze-track">
                                {{#each shop_by_brand}}
                                    <a href="{{url}}" class='_brand-item' aria-label="{{name}}">
                                        <span class="_brand-img-wrapper">
                                            <img class='_brand-logo' data-brand-id="{{id}}" src="data:image/svg+xml,%3Csvg xmlns='http://www.w3.org/2000/svg' %3E%3C/svg%3E" alt="{{name}}" />
                                        </span>
                                        <span class='_brand-name'>{{name}}</span>
                                    </a>
                                {{/each}}
                            </div>
                        </div>
                        <button class="blaze-button blaze-next" aria-label="{{lang 'common.next'}}"></button>
                    </div>
                </div>
            </div>
        {{else}}
            <div class="beautify__section kitchenary__featuredBrands {{#if index}}beautify__section--index{{index}}{{/if}}">
                <div class="container">
                    {{#if theme_settings.featured_brands_heading}}
                        <h2 class="page-heading">{{theme_settings.featured_brands_heading}}</h2>
                    {{else}}
                        <h2 class="page-heading is-srOnly">{{lang 'brand.featured_brands'}}</h2>
                    {{/if}}
                    <ul class="featuredBrands-list">
                        {{#each shop_by_brand}}
                            <li class="featuredBrands-item">
                                <a href="{{url}}">{{name}}</a>
                            </li>
                        {{/each}}
                    </ul>
                </div>
            </div>
        {{/if}}
    {{/if}}

{{else if section '===' 'newsletter'}}
    {{#if settings.show_newsletter_box}}
        {{> components/beautify/section-home-newsletter}}
    {{/if}}
{{/if}}

layout/instantload-base:
{{> components/supermarket/common/global-vars}}
<div id='instantload-html-element'>
    <div id="instantload-head-element">
        <title>
            {{#if head.title}}
                {{ head.title }}
            {{else}}
                {{#if page_type '===' 'product'}}
                    {{#if product.page_title}}
                        {{product.page_title}}
                    {{else}} 
                        {{product.title}} - {{settings.store_name}}
                    {{/if}}
                {{else if page_type '===' 'category'}}
                    {{category.name}} - {{settings.store_name}}
                {{else if page_type '===' 'brands'}}
                    {{settings.store_name}} {{lang 'brand.label'}}
                {{else if page_type '===' 'brand'}}
                    {{brand.name}} - {{settings.store_name}}
                {{else if page}}
                    {{page.title}} - {{settings.store_name}}
                {{else}}
                    {{settings.store_name}}
                {{/if}}
            {{/if}}
        </title>

        {{{ resourceHints }}}
        {{{ head.meta_tags }}}
        {{{ head.config }}}
        {{#block "head"}} {{/block}}

        {{! Include Captcha because BC doesn't include it from Ajax requests }}
        {{#if product.reviews.captcha}}
            <script src="https://www.google.com/recaptcha/api.js" async defer></script>
        {{/if}}

        {{{head.scripts}}}

        {{!-- Theme settings: --}}
        {{~inject 'add_to_cart_popup' theme_settings.add_to_cart_popup}}
        {{~inject 'redirect_cart' theme_settings.redirect_cart}}
        {{~inject 'alsobought_thumbnail_size' theme_settings.alsobought_thumbnail_size}}
        {{~inject 'pdp_price_label' theme_settings.pdp-price-label}}
        {{~inject 'pdp_sale_price_label' theme_settings.pdp-sale-price-label}}
        {{~inject 'pdp_non_sale_price_label' theme_settings.pdp-non-sale-price-label}}
        {{~inject 'pdp_retail_price_label' theme_settings.pdp-retail-price-label}}
        {{~inject 'alsobought_checked' theme_settings.alsobought_checked}}
        {{~inject 'homepage_show_carousel' theme_settings.homepage_show_carousel}}
        {{~inject 'product_new_count' theme_settings.product_new_count}}
        {{~inject 'product_featured_count' theme_settings.product_featured_count}}
        {{~inject 'product_top_count' theme_settings.product_top_count}}
        {{~inject 'homepage_blog_posts_count' theme_settings.homepage_blog_posts_count}}
        {{~inject 'sidebar_shop_by_brand' theme_settings.sidebar_shop_by_brand}}
        {{~inject 'productpage_videos_count' theme_settings.productpage_videos_count}}
        {{~inject 'productpage_reviews_count' theme_settings.productpage_reviews_count}}
        {{~inject 'productpage_related_products_count' theme_settings.productpage_related_products_count}}
        {{~inject 'productpage_similar_by_views_count' theme_settings.productpage_similar_by_views_count}}
        {{~inject 'categorypage_products_per_page' theme_settings.categorypage_products_per_page}}
        {{~inject 'brandpage_products_per_page' theme_settings.brandpage_products_per_page}}
        {{~inject 'categorypage_search' theme_settings.categorypage_search}}
        {{~inject 'categorypage_search_subs' theme_settings.categorypage_search_subs}}
        {{~inject 'instantload' theme_settings.instantload}}
        {{~inject 'navPages_animation' theme_settings.navPages_animation}}
        {{~inject 'navPages_standard' theme_settings.navPages_standard}}
        {{~inject 'show_bulk_order_mode' theme_settings.show_bulk_order_mode}}
        {{~inject 'swatch_option_display_type' theme_settings.swatch_option_display_type}}
        {{~inject 'zoom_size' theme_settings.zoom_size}}
        {{~inject 'product_size' theme_settings.product_size}}
        {{~inject 'product_image_responsive' theme_settings.product_image_responsive}}
        {{!-- // --}}
        {{~inject 'genericError' (lang 'common.generic_error')}}
        {{~inject 'urls' urls}}
        {{~inject 'secureBaseUrl' settings.secure_base_url}}
        {{~inject 'cartId' cart_id}}
        {{~inject 'template' template}}
        {{~inject 'pageType' page_type}}
        {{~inject 'validationDictionaryJSON' (langJson 'validation_messages')}}
        {{~inject 'validationFallbackDictionaryJSON' (langJson 'validation_fallback_messages')}}
        {{~inject 'validationDefaultDictionaryJSON' (langJson 'validation_default_messages')}}
        {{~inject 'ajaxAddToCartEnterQty' (lang 'bulk_order.validate.enter_qty')}}
        {{~inject 'bulkOrderEnterQty' (lang 'bulk_order.validate.enter_qty')}}
        {{~inject 'bulkOrderChooseOptions' (lang 'bulk_order.validate.choose_options')}}
        {{~inject 'txtMinQty' (lang 'products.quantity_min' quantity='%qty%')}}
        {{~inject 'txtMaxQty' (lang 'products.quantity_max' quantity='%qty%')}}
        {{~inject 'compareAddonLang_compare' (lang 'common.compare')}}
        {{~inject 'compareAddonLang_quick_view' (lang 'products.quick_view')}}
        {{~inject 'compareAddonLang_remove' (lang 'compare.remove')}}
        {{~inject 'compareAddonLang_clear_all' (lang 'compare.clear_all')}}
        {{~inject 'categoryId' category.id}}
        {{~inject 'customerRecentlyViewedProductIds' (pluck customer.recently_viewed_products.items 'id')}}
        {{~inject 'customerId' customer.id}}
        {{~inject 'graphQLToken' settings.storefront_api.token}}
        {{~inject 'currencyCode' currency_selector.active_currency_code}}
        {{~inject 'hidePriceFromGuests' settings.hide_price_from_guests}}
        {{~inject 'isCrawlerAndSpeedTestMode' (getVar 'isCrawlerAndSpeedTestMode')}}
        {{~inject 'hidePriceFromGuests' settings.hide_price_from_guests}}
        {{~inject 'outOfStockDefaultMessage' (lang 'products.out_of_stock_default_message')}}
        {{~inject 'pdp_price_label' theme_settings.pdp-price-label}}
        {{~inject 'pdp_sale_price_label' theme_settings.pdp-sale-price-label}}
        {{~inject 'pdp_non_sale_price_label' theme_settings.pdp-non-sale-price-label}}
        {{~inject 'pdp_retail_price_label' theme_settings.pdp-retail-price-label}}
        {{{snippet 'htmlhead'}}}
    </div>
    <div id="instantload-body-element" class="csscolumns supermarket-layout--{{theme_settings.layout}} 
        supermarket-pageType--{{dashcase page_type}} 
        supermarket-page--{{dashcase template}}
        {{#if theme_settings.hide_content_navigation}}supermarket--hideContentNav{{/if}}
        {{#if product_results.faceted_search_enabled}}has-faceted{{/if}}
        {{#if category.faceted_search_enabled}}has-faceted{{/if}}
        {{#if brands.faceted_search_enabled}}has-faceted{{/if}}
        {{#if brand.faceted_search_enabled}}has-faceted{{/if}}
        {{#if result_count '>' 0}}has-result{{/if}}
        " id="topOfPage">

        {{#if banners.top_metadata}}
        <div class="banners" data-banner-location="top" id="instantload-banners-top">
            {{#if settings.data_tag_enabled}}
                {{#each banners.top_metadata}}
                    <div class="banner">
                        {{{this.content}}}
                    </div>
                {{/each}}
            {{else}}
                {{#each banners.top_metadata}}
                    <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-banner-id="{{this.banner-name}}">
                        <div data-event-type="promotion-click">
                            {{{this.content}}}
                        </div>
                    </div>
                {{/each}}
            {{/if}}
        </div>
        {{/if}}

        <div class="body" data-currency-code="{{currency_selector.active_currency_code}}" id="instantload-page-body">
            {{#block "hero"}} {{/block}}
            <div class="container">
                {{#block "page"}} {{/block}}
            </div>
            {{> components/common/modal}}
            {{> components/common/alert-modal}}
        </div>


        {{#if banners.bottom}}
            {{#if settings.data_tag_enabled}}
                <div class="banners banners--bottom" data-banner-location="bottom" id="instantload-banners-bottom">
                    <div class="container">
                        {{#each (limit banners.bottom_metadata 1)}}
                            <div class="banner" data-event-type="promotion" data-entity-id="{{this.id}}" data-name="{{this.banner-name}}" data-position="{{this.location}}" data-name="{{this.banner-name}}">
                                <div data-event-type="promotion-click">
                                    {{{this.content}}}
                                </div>
                            </div>
                        {{/each}}
                    </div>
                </div>
            {{else}}
                <div class="banners banners--bottom" data-banner-location="bottom" id="instantload-banners-bottom">
                    <div class="container">
                        <div class="banner">
                            {{{limit banners.bottom 1}}}
                        </div>
                    </div>
                </div>
            {{/if}}
        {{/if}}

        <script id="instantload-script">
            // Exported in app.js
            (function() {
                var parentContext = window.themeInstantLoad_jsContext || {};
                var context = JSON.parse({{jsContext}} || '{}');
                Object.keys(context).forEach(function(key) {
                    if (context[key] === null && parentContext[key] !== undefined) {
                        context[key] = parentContext[key];
                    }
                });
                window.stencilBootstrap("{{page_type}}", JSON.stringify(context), false).load();
            })();
        </script>

        {{{footer.scripts}}}
        {{{snippet 'footer'}}}
    </div>
</div>

components/carousel:
{{inject 'hasMainCarousel' true}}
<section class="heroCarousel-container">
    <div class="heroCarousel heroCarousel--{{theme_settings.homepage_carousel_style}} blaze-slider" 
        data-blaze-slider='{
                            "all": {
                                "enablePagination": true,
                                "enableAutoplay": true,
                                "autoplayInterval": {{carousel.swap_frequency}},
                                "transitionDuration": 300,
                                "slidesToShow": 1,
                                "slidesToScroll": 1,
                                "slideGap": "0px",
                                "loop": true
                            }
                        }'>
        <div class="blaze-container">
            <div class="blaze-track-container">
                <div class="blaze-track">
                    {{#each carousel.slides}}
                        <div class="heroCarousel-slide heroCarousel-slide--{{@index}}{{#if @first}} heroCarousel-slide--first{{/if}}" tabindex="0">
                            <div class="heroCarousel-image-wrapper{{#and image_height image_width}} heroCarousel-image-wrapper--sized{{/and}}">
                                <a href="{{url}}" tabindex="-1">
                                    <div class="heroCarousel-image-box" style="{{#if image_width}}width:{{image_width}}px{{/if}}">
                                        {{#and image_height image_width}}<div class="heroCarousel-image-box-padding" style="padding-bottom:{{multiply (divide image_height image_width) 100}}%">{{/and}}
                                            {{#if @first}}
                                                {{> components/common/responsive-img
                                                    image=stencil_image
                                                    class="heroCarousel-image"
                                                    fallback_size='1920w'
                                                    lazyload='disabled'
                                                    otherAttributes=(concat (concat (concat (concat 'width="' image_width) '" height="') image_height) '"')
                                                }}
                                            {{else}}
                                                {{> components/common/responsive-img
                                                    image=stencil_image
                                                    class="heroCarousel-image"
                                                    fallback_size='1920w'
                                                    lazyload='lazyload'
                                                    otherAttributes=(concat (concat (concat (concat 'width="' image_width) '" height="') image_height) '"')
                                                }}
                                            {{/if}}
                                        {{#and image_height image_width}}</div>{{/and}}
                                        {{#if heading}}
                                            {{> components/carousel-content-heading-button show_background=true theme_settings=../../theme_settings}}
                                        {{else}}
                                            {{#if button_text}}
                                                {{> components/carousel-content-heading-button show_background=false theme_settings=../../../../theme_settings}}
                                            {{/if}}
                                        {{/if}}
                                    </div>
                                    {{#if heading}}
                                        {{> components/carousel-content show_background=true theme_settings=../../theme_settings}}
                                    {{else}}
                                        {{#if text}}
                                            {{> components/carousel-content show_background=true theme_settings=../../../theme_settings}}
                                        {{else}}
                                            {{#if button_text}}
                                                {{> components/carousel-content show_background=false theme_settings=../../../../theme_settings}}
                                            {{/if}}
                                        {{/if}}
                                    {{/if}}
                                    {{#if text}}
                                        {{> components/carousel-content-desc show_background=true theme_settings=../../../theme_settings}}
                                    {{/if}}
                                </a>
                            </div>
                        </div>
                    {{/each}}
                </div>
            </div>

            <!-- navigation buttons -->
            <button class="blaze-prev" aria-label="{{lang 'common.previous'}}"></button>
            <button class="blaze-next" aria-label="{{lang 'common.next'}}"></button>

            <!-- pagination container -->
            <div class="blaze-pagination"></div>
        </div>
    </div>
    <div class="beautify__heroCarouselMobile">
        {{{region name="home_carousel_mobile"}}}
    </div>
</section>

components/products/featured:
{{inject 'hasFeaturedProducts' true}}
{{#unless hide_heading}}
<h2 class="page-heading">{{theme_settings.product_featured_heading}}</h2>
{{/unless}}

{{#if region_name}}
    {{{region name=region_name}}}
{{/if}}

