components/products/quick-view:
{{> components/supermarket/common/global-vars}}
<div class="modal-body quickView productView-scope">
    {{#if theme_settings.variant '==' 'game'}}
        {{> components/products/product-view-game in_quickview=true}}
    {{else}}
        {{> components/products/product-view in_quickview=true}}
    {{/if}}
</div>

components/supermarket/common/global-var:
{{#if theme_settings.csspreload}}
    {{#if settings.request.is_crawler}}
        {{assignVar 'isCrawlerAndSpeedTestMode' 1}}
    {{/if}}
{{/if}}
{{assignVar 'productCardImageSizeAttrs' (get 'string' (concat 'width=' (join (split theme_settings.productgallery_size 'x') ' height=')))}}
{{assignVar 'categoryImageSizeAttrs' (get 'string' (concat 'width=' (join (split theme_settings.categorythumb_size 'x') ' height=')))}}

components/products/product-view:
{{assignVar 'productImageSizeAttrs' (get 'string' (concat 'width=' (join (split theme_settings.product_size 'x') ' height=')))}}
{{assignVar 'productThumbSizeAttrs' (get 'string' (concat 'width=' (join (split theme_settings.productthumb_size 'x') ' height=')))}}
<div class="productView"
    {{#if settings.data_tag_enabled}}
        data-event-type="product"
        data-entity-id="{{product.id}}"
        data-name="{{product.title}}"
        data-product-category="{{#each product.category}}{{#if @last}}{{this}}{{else}}{{this}}, {{/if}}{{/each}}"
        data-product-brand="{{product.brand.name}}"
        data-product-price="{{#or customer (unless settings.hide_price_from_guests)}}{{#if product.price.with_tax}}{{product.price.with_tax.value}}{{else}}{{product.price.without_tax.value}}{{/if}}{{/or}}"
        data-product-variant="single-product-option"
    {{/if}}
>
    {{#each product.reviews.messages}}
        {{#if error}}
            {{>components/common/alert-error error}}
        {{/if}}
        {{#if success}}
            {{>components/common/alert-success success}}
        {{/if}}
    {{/each}}

    <div class="productView-headingWrapper" data-also-bought-parent-scope>
        {{#unless in_quickview}}
            <div class="container">
        {{/unless}}
                {{#if product.brand}}
                <h5 class="productView-brand">
                    <a href="{{product.brand.url}}">
                        <span>
                            {{#and theme_settings.productpage_show_brand_img gql.data.site.product.brand.defaultImage.url}}
                                <img class="productView-brandImg"
                                    src="data:image/gif;base64,R0lGODlhAQABAIAAAP///wAAACH5BAEAAAAALAAAAAABAAEAAAICRAEAOw=="
                                    srcset="{{gql.data.site.product.brand.defaultImage.url}}"
                                    data-brand-image="{{gql.data.site.product.brand.defaultImage.urlOriginal}}"
                                    data-sizes="{{theme_settings.pdp_brand_size}}"
                                    alt="{{product.brand.name}}" />
                            {{else}}
                                {{product.brand.name}}
                            {{/and}}
                        </span>
                    </a>
                </h5>
                {{/if}}
                <div class="productView-titleWrapper">
                    <h1 class="productView-title">{{product.title}}</h1>
                </div>
                <div class="productView-reviewBrand">
                    <div class="productView-rating">
                        {{#if settings.show_product_rating}}
                            <div class="productView-icon-rating">
                                {{#if product.num_reviews '>' 0}}
                                    <a href="{{product.url}}#tab-reviews" title="{{lang 'products.reviews.link_to_review' total=product.num_reviews}}">
                                        {{> components/products/ratings rating=product.rating}}
                                    </a>
                                {{else}}
                                    {{> components/products/ratings rating=product.rating}}
                                {{/if}}
                            </div>
                            <span class="productView-reviewLink">
                                {{#if product.num_reviews '>' 0}}
                                    <a href="{{product.url}}#tab-reviews" title="{{lang 'products.reviews.link_to_review' total=product.num_reviews}}">
                                        <span class="u-hideMobile u-hideTablet">{{lang 'products.reviews.link_to_review' total=product.num_reviews}}</span><span class="u-hideDesktop">({{product.num_reviews}})</span>
                                    </a>
                                {{else}}
                                    {{lang 'products.reviews.link_to_review' total=product.num_reviews}}
                                {{/if}}
                            </span>
                        {{/if}}
                        {{#if settings.show_product_reviews}}
                            <span class="productView-reviewLink">
                                <a href="{{product.url}}{{#if is_ajax}}#write_review{{/if}}"
                                    {{#unless is_ajax}}
                                        data-reveal-id="modal-review-form"
                                    {{else}}
                                        {{#unless in_quickview}}
                                            data-reveal-id="modal-review-form"
                                        {{/unless}}
                                    {{/unless}}
                                >
                                {{lang 'products.reviews.new'}}
                                </a>
                            </span>
                            {{> components/products/modals/writeReview}}
                        {{/if}}
                    </div>

                    {{#if theme_settings.contact_url}}
                        <a class="productView-reviewBrand-item" href="{{theme_settings.contact_url}}" data-product-url="{{product.url}}" data-papathemes-popup data-template="papathemes/ask-question">{{lang 'common.ask_question'}}</a>
                    {{/if}}
                    {{#filter product.custom_fields '__guide_popup' property='name'}}
                        <a class="productView-reviewBrand-item" href="{{last (split value '|')}}" data-papathemes-popup>{{first (split value '|')}}</a>
                    {{/filter}}
                </div>
        {{#unless in_quickview}}
            </div><!-- .container -->
        {{/unless}}
    </div>

    <div class="productView-detailsWrapper">
        {{#unless in_quickview}}
            <div class="container">
        {{/unless}}
                <div class="productView-beforeAlsoBought">
                    <section data-also-bought-parent-scope class="productView-images
                        {{#if theme_settings.productpage_images_carousel '>' 2}}
                            productView-images--bottom
                        {{else}}
                            {{#unless theme_settings.productpage_vertical_thumbnails}}
                                productView-images--bottom
                            {{/unless}}
                        {{/if}}">
                        <div data-image-gallery class="productView-images-container {{#if theme_settings.productpage_vertical_thumbnails}}_vertical{{else}}_horizontal{{/if}} {{#if theme_settings.productpage_images_carousel '>' 1}}_grid{{/if}}">
                            {{!--
                                Note that these image sizes are coupled to image sizes used in /assets/js/theme/common/product-details.js
                                for variant/rule image replacement
                            --}}

                            {{#if theme_settings.productpage_videos_position '===' 'images'}}
                                {{#if product.videos.list}}
                                    {{assignVar 'videosInGallery' product.videos.list.length}}
                                {{else}}
                                    {{assignVar 'videosInGallery' 0}}
                                {{/if}}
                            {{else}}
                                {{assignVar 'videosInGallery' 0}}
                            {{/if}}

                            {{#or product.images (getVar 'videosInGallery')}}
                                <div class="productView-imageCarousel-main {{#if theme_settings.productpage_vertical_thumbnails}}_vertical{{else}}_horizontal{{/if}} {{#if theme_settings.productpage_images_carousel '>' 1}}_grid{{/if}}" id="productView-imageCarousel-main-{{product.id}}" data-image-gallery-main>
                                    {{#if theme_settings.product_image_responsive}}
                                        {{#each product.images}}
                                            <div class="productView-imageCarousel-main-item{{#startsWith this.data ../product.main_image.data}} slick-current{{/startsWith}}">
                                                <a href="{{getImageSrcset this 'zoom_size' (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}&amp;imbypass=on"
                                                    data-original-zoom="{{getImageSrcset this 'zoom_size' (cdn ../theme_settings.default_image_product) 1x=../theme_settings.zoom_size}}&amp;imbypass=on"
                                                    data-original-img="{{getImageSrcset this 'product_size' (cdn ../theme_settings.default_image_product) 1x=../theme_settings.product_size}}"
                                                    data-original-srcset="{{getImageSrcset this use_default_sizes=true }}">
                                                    {{#startsWith this.data ../product.main_image.data}}
                                                        {{!-- First image loads default quality image --}}
                                                        {{#if ../../theme_settings.lazyload_mode '==' 'lazyload'}}
                                                            {{!-- Disable lazyload for the first image if mode is not lazyload+lqip --}}
                                                            {{> components/common/responsive-img
                                                                image=this
                                                                fallback_size=../../../theme_settings.product_size
                                                                lazyload='disabled'
                                                                default_image=../../../theme_settings.default_image_product
                                                                lqip_size=../../../theme_settings.product_size
                                                                otherAttributes=(getVar 'productImageSizeAttrs')
                                                            }}
                                                        {{else}}
                                                            {{> components/common/responsive-img
                                                                image=this
                                                                fallback_size=../../../theme_settings.product_size
                                                                lazyload=../../theme_settings.lazyload_mode
                                                                default_image=../../../theme_settings.default_image_product
                                                                lqip_size=../../../theme_settings.product_size
                                                                otherAttributes=(getVar 'productImageSizeAttrs')
                                                            }}
                                                        {{/if}}
                                                    {{else}}
                                                        {{> components/common/responsive-img
                                                            image=this
                                                            fallback_size=../../theme_settings.product_size
                                                            lazyload=../../theme_settings.lazyload_mode
                                                            default_image=../../theme_settings.default_image_product
                                                            otherAttributes=(getVar 'productImageSizeAttrs')
                                                        }}
                                                    {{/startsWith}}
                                                </a>
                                            </div>
                                        {{/each}}
                                    {{else}}
                                        {{#each product.images}}
                                            <div class="productView-imageCarousel-main-item{{#startsWith this.data ../product.main_image.data}} slick-current{{/startsWith}}">
                                                <a href="{{getImage this 'zoom_size' (cdn ../theme_settings.default_image_product)}}?imbypass=on"
                                                    data-original-zoom="{{getImage this 'zoom_size' (cdn ../theme_settings.default_image_product)}}?imbypass=on"
                                                    data-original-img="{{getImage this 'product_size' (cdn ../theme_settings.default_image_product)}}"
                                                    data-original-srcset="">
                                                    {{#if @index '===' 0}}
                                                        <img src="{{getImage this 'product_size' (cdn ../../theme_settings.default_image_product)}}" alt="{{this.alt}}" title="{{this.alt}}" {{getVar 'productImageSizeAttrs'}}>
                                                    {{else}}
                                                        <img data-lazy="{{getImage this 'product_size' (cdn ../../theme_settings.default_image_product)}}" alt="{{this.alt}}" title="{{this.alt}}" {{getVar 'productImageSizeAttrs'}}>
                                                    {{/if}}
                                                </a>
                                            </div>
                                        {{/each}}
                                    {{/if}}
                                    {{#if theme_settings.productpage_videos_position '===' 'images'}}
                                        {{#each product.videos.list}}
                                            <div class="productView-imageCarousel-main-item _video">
                                                <a
                                                    href="//i.ytimg.com/vi/{{id}}/hqdefault.jpg"
                                                    data-image-gallery-item
                                                    data-image-gallery-new-image-url="//i.ytimg.com/vi/{{id}}/default.jpg"
                                                    data-image-gallery-zoom-image-url="//i.ytimg.com/vi/{{id}}/default.jpg"
                                                    data-video-url="//www.youtube.com/embed/{{id}}?autoplay=1&mute=1&enablejsapi=1"
                                                    data-video-id="{{id}}">
                                                    <span class="productView-imageCarousel-main-item-img-container yt-video">
                                                        <img src="//i.ytimg.com/vi/{{id}}/hqdefault.jpg" alt="{{title_long}}" title="{{title_long}}">
                                                    </span>
                                                </a>
                                            </div>
                                        {{/each}}
                                    {{/if}}
                                </div>
                            {{else}}
                                <div class="productView-imageCarousel-main" id="productView-imageCarousel-main-{{product.id}}" data-image-gallery-main>
                                    <div class="productView-imageCarousel-main-item slick-current">
                                        {{#if theme_settings.lazyload_mode '==' 'lazyload'}}
                                            {{!-- Disable lazyload for the first image if mode is not lazyload+lqip --}}
                                            {{> components/common/responsive-img
                                                image=product.main_image
                                                fallback_size=theme_settings.product_size
                                                lazyload='disabled'
                                                default_image=theme_settings.default_image_product
                                                otherAttributes=(getVar 'productImageSizeAttrs')
                                            }}
                                        {{else}}
                                            {{> components/common/responsive-img
                                                image=product.main_image
                                                fallback_size=theme_settings.product_size
                                                lazyload=theme_settings.lazyload_mode
                                                default_image=theme_settings.default_image_product
                                                otherAttributes=(getVar 'productImageSizeAttrs')
                                            }}
                                        {{/if}}
                                    </div>
                                </div>
                            {{/or}}

                            {{#if theme_settings.productpage_videos_position '===' 'images'}}
                                {{#if product.videos.list.length}}
                                    <div class="productView-videoPlayer hide" data-video-player>
                                        <div class="productView-videoPlayer-container">
                                            <iframe
                                                type="text/html"
                                                width="815"
                                                height="496"
                                                frameborder="0"
                                                webkitAllowFullScreen
                                                mozallowfullscreen
                                                allowFullScreen
                                                allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture"
                                                src="">
                                            </iframe>
                                        </div>
                                    </div>
                                {{/if}}
                            {{/if}}

                            {{#if theme_settings.product_custom_badges}}
                                {{#filter product.custom_fields '__badge' property='name'}}
                                    <div class="sale-flag-side sale-flag-side--custom">
                                        <span class="sale-text">{{{value}}}</span>
                                    </div>
                                {{/filter}}
                            {{/if}}

                            {{#or (if product.images.length '>' 1) (getVar 'videosInGallery')}}
                                <div class="productView-imageCarousel-nav {{#if theme_settings.productpage_vertical_thumbnails}}_vertical{{else}}_horizontal{{/if}}" id="productView-imageCarousel-nav-{{product.id}}"
                                    data-image-gallery-nav
                                    {{#if theme_settings.productpage_images_carousel '>' 0}}
                                        data-image-gallery-nav-slides="{{theme_settings.productpage_thumbnails_count}}"

                                        {{#unless theme_settings.productpage_vertical_thumbnails}}
                                            data-image-gallery-nav-horizontal="true"
                                        {{/unless}}
                                        {{#if theme_settings.productpage_images_carousel '>' 1}}
                                            data-image-gallery-nav-rows="{{theme_settings.productpage_images_carousel}}"
                                        {{/if}}
                                    {{else}}
                                        data-image-gallery-nav-disable-slider
                                    {{/if}}
                                >
                                    {{#if theme_settings.productpage_images_carousel '===' 1}}
                                        {{#each product.images}}
                                            <div class="productView-imageCarousel-nav-item{{#startsWith this.data ../product.main_image.data}} slick-current{{/startsWith}}">
                                                {{> components/common/responsive-img
                                                    image=this
                                                    fallback_size=../theme_settings.productthumb_size
                                                    lazyload=../theme_settings.lazyload_mode
                                                    otherAttributes=(getVar 'productThumbSizeAttrs')
                                                }}
                                            </div>
                                        {{/each}}
                                        {{#if theme_settings.productpage_videos_position '===' 'images'}}
                                            {{#each product.videos.list}}
                                                <div class="productView-imageCarousel-nav-item _video">
                                                    <button>
                                                        <span class="productView-imageCarousel-nav-item-img-container yt-video">
                                                            <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="//i.ytimg.com/vi/{{id}}/default.jpg" alt="{{title_long}}" title="{{title_long}}">
                                                        </span>
                                                    </button>
                                                </div>
                                            {{/each}}
                                        {{/if}}
                                    {{else}}
                                        {{#each product.images}}
                                            <a class="productView-imageCarousel-nav-item" href="{{getImage this 'zoom_size' (cdn ../theme_settings.default_image_product)}}?imbypass=on">
                                                {{> components/common/responsive-img
                                                    image=this
                                                    fallback_size=../theme_settings.productthumb_size
                                                    lazyload=../theme_settings.lazyload_mode
                                                    otherAttributes=(getVar 'productThumbSizeAttrs')
                                                }}
                                            </a>
                                        {{/each}}
                                        {{#if theme_settings.productpage_videos_position '===' 'images'}}
                                            {{#each product.videos.list}}
                                                <a class="productView-imageCarousel-nav-item _video"
                                                    href="//i.ytimg.com/vi/{{id}}/hqdefault.jpg"
                                                    data-image-gallery-item
                                                    data-image-gallery-new-image-url="//i.ytimg.com/vi/{{id}}/default.jpg"
                                                    data-image-gallery-zoom-image-url="//i.ytimg.com/vi/{{id}}/default.jpg"
                                                    data-video-url="//www.youtube.com/embed/{{id}}?autoplay=1&mute=1&enablejsapi=1"
                                                    data-video-id="{{id}}">
                                                    <span class="productView-imageCarousel-nav-item-img-container yt-video">
                                                        <img class="lazyload" src="{{cdn 'img/loading.svg'}}" data-src="//i.ytimg.com/vi/{{id}}/default.jpg" alt="{{title_long}}" title="{{title_long}}">
                                                    </span>
                                                </a>
                                            {{/each}}
                                        {{/if}}
                                    {{/if}}
                                </div>
                            {{/or}}
                        </div>
                    </section>
                    <div class="productView-details">
                        <section data-also-bought-parent-scope>
                            <div class="productView-product">
                                {{#and theme_settings.show_condition_badge product.condition}}
                                    <div class="productView-conditionLabel _{{dashcase product.condition}}">{{product.condition}}</div>
                                {{/and}}
                                {{assignVar '__countdown_date' 0}}
                                {{assignVar '__countdown_label' 0}}

                                {{#each product.custom_fields}}
                                    {{#if name '===' '__countdown_date'}}
                                        {{assignVar '__countdown_date' value}}
                                    {{else if name '===' '__countdown_label'}}
                                        {{assignVar '__countdown_label' value}}
                                    {{/if}}
                                {{/each}}

                                {{#or product.price.non_sale_price_with_tax product.price.non_sale_price_without_tax}}
                                    {{#if (getVar '__countdown_date')}}
                                        <div class="productView-saleCount" data-countdown="{{getVar '__countdown_date'}}">
                                                {{#if theme_settings.product_sale_badges '===' 'percent'}}
                                                    <div class="_labelTime__title">
                                                        <span>
                                                            {{#if (getVar '__countdown_label')}}
                                                                {{first (split (getVar '__countdown_label') '|')}}
                                                            {{else}}
                                                                {{theme_settings.product_sale_label}}
                                                            {{/if}}
                                                        </span>
                                                        <span class="_priceSale">
                                                            {{#if product.price.rrp_without_tax}}
                                                                {{round (multiply (subtract 1 (divide product.price.without_tax.value product.price.rrp_without_tax.value)) 100)}}%
                                                            {{else if product.price.rrp_with_tax}}
                                                                {{round (multiply (subtract 1 (divide product.price.with_tax.value product.price.rrp_with_tax.value)) 100)}}%
                                                            {{else if product.price.non_sale_price_without_tax}}
                                                                {{round (multiply (subtract 1 (divide product.price.without_tax.value product.price.non_sale_price_without_tax.value)) 100)}}%
                                                            {{else}}
                                                                {{round (multiply (subtract 1 (divide product.price.with_tax.value product.price.non_sale_price_with_tax.value)) 100)}}%
                                                            {{/if}}
                                                        </span>
                                                    </div>
                                                {{else}}
                                                    <div class="_labelTime__title">
                                                        {{#if (getVar '__countdown_label')}}
                                                            {{first (split (getVar '__countdown_label') '|')}}
                                                        {{else}}
                                                            {{theme_settings.product_sale_label}}
                                                        {{/if}}
                                                    </div>
                                                {{/if}}
                                            <div class="_showTimer is-srOnly">
                                                <span class="_labelTime__title">
                                                    {{#if (getVar '__countdown_label')}}
                                                        {{last (split (getVar '__countdown_label') '|')}}:&#32;
                                                    {{/if}}
                                                </span>
                                                <div class="_blockTimer">
                                                    <div class="_blockDays">
                                                        <span class="_days">00</span><span>{{lang 'sale_count.days'}}</span>
                                                    </div>
                                                    <div class="_blockHours">
                                                        <span class="_hours">00</span><span>{{lang 'sale_count.hours'}}</span>
                                                    </div>
                                                    <div class="_blockMinutes">
                                                        <span class="_minutes">00</span><span>{{lang 'sale_count.minutes'}}</span>
                                                    </div>
                                                    <div class="_blockSeconds">
                                                        <span class="_seconds">00</span><span>{{lang 'sale_count.seconds'}}</span>
                                                    </div>
                                                </div>
                                            </div>
                                        </div>
                                    {{/if}}
                                {{/or}}
                                <div class="productView-titleWrapper is-srOnly">
                                    <h1 class="productView-title">{{product.title}}</h1>
                                </div>

                                <div class="productView-price">
                                    {{#if product.call_for_price}}
                                        <span class="price--call">{{product.call_for_price}}</span>
                                    {{else}}
                                        {{#or customer (unless settings.hide_price_from_guests)}}
                                            {{> components/products/price price=product.price}}
                                        {{else}}
                                            {{> components/common/login-for-pricing}}
                                        {{/or}}
                                    {{/if}}
                                    {{#unless (getVar '__countdown_date')}}
                                        {{#if theme_settings.productpage_show_sale_badge}}
                                            {{#or product.price.non_sale_price_with_tax product.price.non_sale_price_without_tax}}
                                                {{#if theme_settings.product_sale_badges '===' 'percent'}}
                                                    <div class="productView-saleLabel _percent">
                                                        {{#replace '{percent}' theme_settings.product_sale_label}}
                                                            {{#if product.price.non_sale_price_without_tax}}
                                                                {{round (multiply (subtract 1 (divide product.price.without_tax.value product.price.non_sale_price_without_tax.value)) 100)}}%
                                                            {{else}}
                                                                {{round (multiply (subtract 1 (divide product.price.with_tax.value product.price.non_sale_price_with_tax.value)) 100)}}%
                                                            {{/if}}
                                                        {{else}}
                                                            {{theme_settings.product_sale_label}}
                                                            <span class="_priceSale">
                                                                {{#if product.price.rrp_without_tax}}
                                                                    {{round (multiply (subtract 1 (divide product.price.without_tax.value product.price.rrp_without_tax.value)) 100)}}%
                                                                {{else if product.price.rrp_with_tax}}
                                                                    {{round (multiply (subtract 1 (divide product.price.with_tax.value product.price.rrp_with_tax.value)) 100)}}%
                                                                {{else if product.price.non_sale_price_without_tax}}
                                                                    {{round (multiply (subtract 1 (divide product.price.without_tax.value product.price.non_sale_price_without_tax.value)) 100)}}%
                                                                {{else}}
                                                                    {{round (multiply (subtract 1 (divide product.price.with_tax.value product.price.non_sale_price_with_tax.value)) 100)}}%
                                                                {{/if}}
                                                            </span>
                                                        {{/replace}}
                                                    </div>
                                                {{else}}
                                                    <div class="productView-saleLabel">
                                                        {{theme_settings.product_sale_label}}
                                                    </div>
                                                {{/if}}
                                            {{/or}}
                                        {{/if}}
                                    {{/unless}}
                                    <div class="_wrapper-price-saving">
                                        <div class="price-section price-section--saving" {{#unless product.price.saved}}style="display: none;"{{/unless}}>
                                            <span class="price">{{lang 'products.you_save_opening_text'}}</span>
                                            <span data-product-price-saved class="price price--saving">{{product.price.saved.formatted}} ({{#if product.price.rrp_without_tax}}{{round (multiply (subtract 1 (divide product.price.without_tax.value product.price.rrp_without_tax.value)) 100)}}%{{else}}{{round (multiply (subtract 1 (divide product.price.with_tax.value product.price.rrp_with_tax.value)) 100)}}%{{/if}})</span><!--
                                            --><span class="price">{{lang 'products.you_save_closing_bracket'}}</span>
                                        </div>
                                    </div>
                                </div>
                                {{#if settings.bulk_discount_enabled}}
                                    {{#or customer (unless settings.hide_price_from_guests)}}
                                        <div class="productView-info-bulkPricing">
                                            {{> components/products/bulk-discount-rates bulk_discount_rates=product.bulk_discount_rates}}
                                        </div>
                                    {{/or}}
                                {{/if}}
                                {{{region name="product_below_price--global"}}}
                                {{{region name="product_below_price"}}}
                            </div>
                            <ul class="productView-info">
                                <li>
                                    <span class="productView-info-name productView-info-name--sku sku-label"{{#unless product.sku}} style="display: none;"{{/unless}}>{{lang 'products.sku'}}</span>
                                    <span class="productView-info-value productView-info-value--sku" data-product-sku>{{product.sku}}</span>
                                </li>
                                {{#if product.upc}}
                                    <li>
                                        <span class="productView-info-name productView-info-name--upc upc-label"{{#unless product.upc}} style="display: none;"{{/unless}}>{{lang 'products.upc'}}</span>
                                        <span class="productView-info-value productView-info-value--upc" data-product-upc>{{product.upc}}</span>
                                    </li>
                                {{/if}}
                                {{#if product.mpn}}
                                    <li>
                                        <span class="productView-info-name productView-info-name--mpn mpn-label" {{#unless product.mpn}} style="display: none;"{{/unless}}>{{lang 'products.mpn'}}</span>
